var accessToken = '232105850.e5e555d.ccdec577f5c447c1ae191897a3344bdf';

var limit = 36; //Limite mï¿½ximo de fotos

var setSize = "small";


var instagram = function() {

	return {

		init: function() {

			instagram.loadImages();

		},

		loadImages: function() {

			var getImagesURL = 'https://api.instagram.com/v1/tags/selfie/media/recent?client_id=e5e555da00124f6790c1d29fac898c09&access_token='+ accessToken +'';

			$.ajax({

				type: "GET",

				dataType: "jsonp",

				cache: false,

				url: getImagesURL,

				success: function(data) {

                    //console.log(data);

					

					if (data.data.length < 100) {

						limit = data.data.length;

					}

					

					for(var i = 0; i < limit ; i+=1) {

                        //console.log(i);

						if(setSize == "small") {

							var size = data.data[i].images.thumbnail.url;

						} else if(setSize == "medium") {

							var size = data.data[i].images.low_resolution.url;

						} else {

							var size = data.data[i].images.standard_resolution.url;	

						}

						$("#instagram").append("<li><a class='photos' rel='instagram' href='" + data.data[i].images.standard_resolution.url +"'><img src='" + size +"'/></a>");

					}

				}

			});

		}

	}

}();



$(document).ready(function() {

    instagram.init();

	

	$('.photos').fancybox();

	

});