var accessToken = '18225166.5b9e1e6.44e64908b9aa4b669c36cb09c7396c70';

var limit = 60; //Limite mï¿½ximo de fotos

var setSize = "small";



var instagram = function() {

	return {

		init: function() {

			instagram.loadImages();

		},

		loadImages: function() {

			var getImagesURL = 'https://api.instagram.com/v1/tags/daniloeloreta/media/recent?client_id=8802c72d82cb4506a039e3078afbea50&access_token='+ accessToken +'';

			$.ajax({

				type: "GET",

				dataType: "jsonp",

				cache: false,

				url: getImagesURL,

				success: function(data) {

                    //console.log(data);

					

					if (data.data.length < 100) {

						limit = data.data.length;

					}

					

					for(var i = 0; i < limit ; i+=1) {

                        //console.log(i);

						if(setSize == "small") {

							var size = data.data[i].images.thumbnail.url;

						} else if(setSize == "medium") {

							var size = data.data[i].images.low_resolution.url;

						} else {

							var size = data.data[i].images.standard_resolution.url;	

						}

						$("#instagram").append("<li><a class='photos' rel='instagram' href='" + data.data[i].images.standard_resolution.url +"'><img src='" + size +"'/></a>");

					}

				}

			});

		}

	}

}();



$(document).ready(function() {

    instagram.init();

	

	$('.photos').fancybox();

	

});